import React, { useState } from 'react';
import { ExcelRenderer } from 'react-excel-renderer';

const ExcelImport = ({ uploadHandler }) => {
  const [errorMessage, setErrorMessage] = useState('');

  const fileHandler = (event) => {
    let fileObj = event.target.files[0];
    
    if (!fileObj) {
      setErrorMessage('No file uploaded!');
      return;
    }

    if (!(fileObj.type === 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' || fileObj.type === 'application/vnd.ms-excel')) {
      setErrorMessage('Unknown file format. Only Excel files are supported.');
      return;
    }

    ExcelRenderer(fileObj, (err, resp) => {
      if (err) {
        console.error(err);
        setErrorMessage('Error reading file');
      } else {
        const { rows } = resp;
        const headers = rows[0];
        const data = rows.slice(1).map(row => {
          let obj = {};
          headers.forEach((header, index) => {
            obj[header] = row[index];
          });
          return obj;
        });
        uploadHandler(data);
      }
    });
  };

  return (
    <div className="excel-import-container">
      <h2>Import Excel File</h2>
      <div className="file-upload">
        <input
          type="file"
          onChange={fileHandler}
          accept=".xlsx, .xls"
        />
      </div>
      {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
    </div>
  );
};

export default ExcelImport;
